package com.lightning.animtb.component;

import java.awt.Graphics;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.util.Base64;

import javax.imageio.ImageIO;

import com.lightning.animtb.main.Main;

public class Text extends Component {
	private static BufferedImage font;
	
	static {
		try {
			font = ImageIO.read(new ByteArrayInputStream(Base64.getDecoder().decode("")));
		} catch(Exception e) {
			font = null;
		}
	}
	
	private String text;
	
	public Text(String text) {
		super(text.length()*8, 16);
		this.text = text;
	}
	
	public Text(String text, int x, int y) {
		super(x, y, text.length()*8, 16);
		this.text = text;
	}
	
	public void renderThis(boolean halfBright) {
		BufferedImage target = Main.display;
		Graphics g = target.getGraphics();
		for(int i = 0; i < text.length(); i++) {
			char c = text.charAt(i);
			int cx = (int)c & 0x0f;
			int cy = (int)c & 0x70;
			g.drawImage(font.getSubimage(cx*8, cy, 8, 16), x+i*8, y, null);
		}
	}
	
	public boolean mousePressedThis(int mouseX, int mouseY) {return false;}
	
	public boolean mouseReleasedThis(int mouseX, int mouseY) {return false;}
}
